import React from "react";
import styles from './IntroPic.module.css'

const IntroPic = () => {
    return (
        <div className={styles.intro}>
            <div className={styles.imgText}>
                <h1>A World of Knowledge at Your Fingertips</h1>
            </div>
        </div>
    )
}
export default IntroPic;
